script.String:
width = 1024;   // 32 tiles
height = 608;   // 19 tiles
tile_size = 32; // Default tile size

let ts = tile_size;
let gold = rgb(200, 200, 150);
let silver = rgb(192, 192, 192);
let tm1 = tilemaps.get("UIParts1");
let frame_start = tm1.get_tile(3, 0);
let movement_id = "";
let movement_visible = 0;
let font_name = "VT323-Regular";

message.status("Eldiron Demo v0.1. Work in progress.");

// Draw the screen
fn draw_screen() {
  draw.rect(rect(0, 0, width, height), rgb(0, 0, 0)); // Black background

  // Header
  draw.tile_sat(pos(9 * ts, 2 * ts), tm1.get_tile(0,7), gold);
  draw.tile_sat(pos(10 * ts, 2 * ts), tm1.get_tile(1, 7), gold);
  draw.tile_sat(pos(11 * ts, 2 * ts), tm1.get_tile(2, 7), gold);
  draw.text(pos(13 * ts + 5, 2 * ts), "Eldiron", "eldiron", 40.0, silver);
  draw.tile_sat(pos(19 * ts, 2 * ts), tm1.get_tile(3, 7), gold);
  draw.tile_sat(pos(20 * ts, 2 * ts), tm1.get_tile(4, 7), gold);
  draw.tile_sat(pos(21 * ts, 2 * ts), tm1.get_tile(5, 7), gold);
  draw.text(pos(5 * ts + 16, 4 * ts + 10), "Enter the castle, city and dungeon to learn more about Eldiron!", font_name, 25.0, silver);
  draw.text(pos(6 * ts + 20, 5 * ts + 20), "Tell your story and create your own classical & fun RPG.", font_name, 25.0, silver);
  draw.text(pos(10 * ts, 7 * ts), "For more info visit Eldiron.com", font_name, 25.0, silver);
  // Game
  let game_x = 6 * ts;
  let game_y = 10 * ts;
  draw.game(rect(game_x, game_y, 19 * ts, 5 * ts));
  draw.frame_sat(rect(game_x - ts, game_y - ts, 21 * ts, 7 * ts), gold, frame_start);

  // Movement control
  let offset = 0; if movement_id == "west" { offset = 1; };
  draw.tile_sat(pos(1 * ts, 11 * ts), tm1.get_tile(1 + offset, 8), gold);
  offset = 0; if movement_id == "north" { offset = 1; };
  draw.tile_sat(pos(2 * ts, 10 * ts), tm1.get_tile(7 + offset, 6), gold);
  offset = 0; if movement_id == "east" { offset = 1; };
  draw.tile_sat(pos(3 * ts, 11 * ts), tm1.get_tile(4 + offset, 8), gold);
  offset = 0; if movement_id == "south" { offset = 1; };
  draw.tile_sat(pos(2 * ts, 12 * ts), tm1.get_tile(7 + offset, 7), gold);
  movement_visible -= 1;
  if movement_visible <= 0 { movement_id = ""; }

  // Messages
  draw.messages(rect(5*ts, 520, width - 80, 80), font_name, 20.0, silver);
}

// Handle the mouse events
fn touch_down(x, y) {
  let tx = x / tile_size; let ty = y / tile_size;

  let dir = "";
  if tx == 1 && ty == 11 {
    dir = "west"
  } else
  if tx == 2 && ty == 10 {
    dir = "north";
  } else
  if tx == 3 && ty == 11 {
    dir = "east";
  } else
  if tx == 2 && ty == 12 {
    dir = "south";
  }

  if dir.len() > 0 {
    movement_id = dir;
    movement_visible = 2;
    cmd.move(dir);
  }
}

// Handle key down events
fn key_down(key) {
  let dir = "";
  if key == "left" {
    dir = "west";
  } else
  if key == "right" {
    dir = "east";
  } else
  if key == "up" {
    dir = "north";
  } else
  if key == "down" {
    dir = "south";
  }

  if dir.len() > 0 {
    movement_id = dir;
    movement_visible = 2;
    cmd.move(dir);
  }
}
